package com.zeroquo.dottchallengescala.dataprovider

import java.time.{LocalDate, LocalDateTime, LocalTime, Month}
import java.util.concurrent.ThreadLocalRandom

import com.zeroquo.dottchallengescala.usecase.entity.Item
import com.zeroquo.dottchallengescala.usecase.entity.Order

class OrderDataProvider (itemDataProvider: ItemDataProvider = new ItemDataProvider()){

  def getAllOrders : List[Order] = generateOrders

  private def generateOrders: List[Order] = {

    def calculatePrice(items: List[Item]) : BigDecimal =
      items.map(item => item.cost + item.shippingFee + item.taxAmount).sum

    def getRandomOrderDate: LocalDateTime = {
      val minDay = LocalDate.of(2017, Month.JANUARY.getValue, 1).toEpochDay
      val maxDay = LocalDate.now.toEpochDay
      val randomDay = ThreadLocalRandom.current.nextLong(minDay, maxDay)
      val randomDate = LocalDate.ofEpochDay(randomDay)
      LocalDateTime.of(randomDate, LocalTime.now)
    }

    getNames.map(name => {
      val list = itemDataProvider.generateRandomItemsList
      Order(customerName = name, contact = s"$name@mail.com", shippingAddress = "someAddress", calculatePrice(list), getRandomOrderDate, list)
    })
  }

